css:
.address, .print-format td {
    padding: 0px !important;
}

#footer-html {
    position: fixed;
    bottom: 0; 
    left: 0;
    right: 0;
    width: 100%;
}

.salary-slip {
  font-family: Arial, sans-serif;
  color: black;
  width: 99%;
  height: 100%;
  margin: 0px 2px;
  font-size: 12px;
}

.header h4 {
  margin-top: 10px;
  margin-bottom: 5px;
  font-size: 14px !important;
}

.text-center {
  text-align: center;
}

.text-right {
  text-align: right;
}

.table {
    border-collapse: collapse;
}

.employee-details td, .earnings td, .deduction td {
  border: 1px solid black;
  padding: 2px !important;
  color: black;
  font-family: Arial, sans-serif;
  font-size: 12px;
}

.employee-code td {
    border: none;
    padding: 0px !important;
    color: black;
    font-family: Arial, sans-serif;
    font-size: 12px;
}

.net-pay {
  border: 1px solid black;
  font-weight: bold;
}

.footer-content {
  text-align: center;
  margin-top: 10px !important;
  position: relative; 
  margin-top: 50px; 
  clear: both;
}
